sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
	if(newValue === 'KGF'){
		g_form.addOption('selecting_city','1','Bangalore');
		g_form.addOption('selecting_city','2','Mysore');
		g_form.addOption('selecting_city','3','Shivamogga');
	}
	else{
		g_form.addOption('selecting_city','1','Bangalore');
		g_form.addOption('selecting_city','2','Hubli');
		g_form.addOption('selecting_city','3','Ballary')
	}

}
