sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below

    var selecttheater = g_form.getValue('select_theater');

    // Clear all of the choices from the What needed field choice list
    g_form.clearOptions('select_theater');

    if(newValue == 'bengaluru'){
      g_form.addOption('select_theater','theater 1','Theater 1');
      g_form.addOption('select_theater','theater 2','Theater 2');
      g_form.addOption('select_theater','theater 3','Theater 3');
//       g_form.addOption('select_theater','other','Other');
    }

    if(newValue == 'mysore'){
      g_form.addOption('select_theater','theater 4','Theater 4');
      g_form.addOption('select_theater','theater 5','Theater 5');
      g_form.addOption('select_theater','theater 6','Theater 6');
//       g_form.addOption('select_theater','other','Other');
    }

    if(newValue == 'shivamogga'){
     g_form.addOption('select_theater','theater 7','Theater 7');
      g_form.addOption('select_theater','theater 8','Theater 8');
      g_form.addOption('select_theater','theater 9','Theater 9');
//       g_form.addOption('select_theater','other','Other');
    }

    if(isLoading && !g_form.isNewRecord()){
      g_form.setValue('select_theater', selecttheater);
    }
  }
